import { useState, useEffect } from "react";
import React from 'react';
import { Map, Marker, ZoomControl, /*Overlay*/ } from "pigeon-maps"
import { maptiler } from 'pigeon-maps/providers'

import Menu from './components/Menu';
import Sidebar from './components/Sidebar';
import Mouse from "./components/Mouse";

function App() {
  //son 34 gatas

  const maptilerProvider = maptiler('OeC7UjZWzAPnrhmLpPbX', 'basic-v2');
  const [mapCenter, setMapCenter] = useState([3.451245219418733, -76.54367205437333])
  const [mapZoom, setMapZoom] = useState(18)

  const color="#99F";
  const gatas = [
    {
      id: 1,
      nombre:"Kiriyaku: Gata Vigía del Río",
      photoURL:"https://2.bp.blogspot.com/-ydcAWUMCZoA/VKXnjegNWkI/AAAAAAAAJY0/g_-mX7aFtus/s1600/Gata%2BKuriyaku%2B-%2BCarlos%2BJacanamijoy%2B-%2B2.JPG",
      coord: [3.451310447643775, -76.54317536931384],
      desc: "Gata protectora de los ríos del territorio nacional que albergan nuestro oro. Viene del rio \"Kiriyaku\" del Putumayo, cuyo nombre significa \"Rio de Oro\" en Quechua. Le gusta merodear por las orillas del río Cali",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
    {
      id: 2,
      nombre:"La Gata Coqueta",
      photoURL:"https://3.bp.blogspot.com/-bSrrymfn3Fk/VKXnvYVousI/AAAAAAAAJaY/me02UEWUWbo/s1600/La%2Bgata%2BCoqueta%2B-%2BMariapaz%2BJaramillo%2B-%2B2.JPG",
      coord: [3.4512182029859604, -76.54315780951954],
      desc: "Quiero demostrar la coquetería y la independencia que tienen los gatos. Por eso su nombre: \"La Gata Coqueta\" en memoria del maestro Tejada que hizo ese bello gato para la ciudad de Cali, y el cual seguramente necesita una compañera muy coqueta y atrevida que esté a la altura de tan querido minino. Los colores son fuertes, los cuales siempre he trabajado.",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
      color: "tomato"
    },
    {
      id: 3,
      nombre:"Gata Ceremonial",
      photoURL:"https://3.bp.blogspot.com/-3dMfdx8I3FA/VLkuG-ALK_I/AAAAAAAAKJc/orrSPHDP47Y/s1600/La%2Bgata%2BCeremonial%2B-%2BPedro%2BAlc%C3%A1ntara%2BHerr%C3%A1n%2B-%2B2.jpg",
      coord: [3.4512812413279104, -76.54341493194644],
      desc: "Esta Gata se caracteriza por el uso de elementos desprendidos de nuestra trietnia cultural, en la que se funden formas tradicionales de la pintura con diseños de origen precolombino evidenciados en las formas de animales que la circundan",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
      color: "dark-orange"
    },
    {
      id: 4,
      nombre:"Gata Vellocino de Oro",
      photoURL:"https://1.bp.blogspot.com/-uk1aPnEMw4o/VKXZtS2vdtI/AAAAAAAAJWs/1cRNxyZ6ov8/s1600/La%2Bgata%2BVellocino%2Bde%2Boro%2B-%2BJos%C3%A9%2BHoracio%2BMart%C3%ADnez%2B-%2B1.JPG",
      coord: [3.4511551646398115, -76.54351868310115],
      desc: "Esta gata elaborada en laminilla de oro de 22 kilates motiva que las personas se acerquen a ella, seducidas con el resplandor del efímero brillo. ¿Será que el Gato del Río se rinde a esta Gata de oropel y opulencia?",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
      color: "orange"
    },
    {
      id: 5,
      nombre:"Gata Gachuza",
      photoURL:"https://1.bp.blogspot.com/-x-9T_S8CiC8/VKXZoOO19HI/AAAAAAAAJV8/jnv45S8JvIo/s1600/La%2Bgata%2BGachuza%2B-%2BAngela%2BVillegas%2B-%2B1.JPG",
      coord: [3.4513127604973146, -76.54376227276875],
      desc: "Ella quiere defenderse de la agresión del medio ambiente, de los gatos callejeros que la ven irresistible por su color rojo y coquetería maléfica, pero a su vez, como el puerco espín, está alerta, solo tiene ojos para su gato, el del \"Río\"",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
      color: "red"
    },
  ];
  const [sidedata, setSidedata] = useState({});
  useEffect(() => {
    if(Object.keys(sidedata).length !== 0){
      //console.log(sidedata.nombre)
      document.getElementById("demo").classList.add("show");
    }
  }, [sidedata]);

  return (
    <div className="bg-dark bg-gradient" style={{height: "100hv", width: "100wv"}}>
    <Mouse/>
    <Menu />
    <Sidebar data={sidedata}/>
    <div className="map" style={{height: "89.6vh"}}>
    <Map 
    provider={maptilerProvider}
    dprs={[1, 2]}
    defaultCenter={mapCenter}
    defaultZoom={mapZoom}
    >
      {/*onBoundsChanged={({ center, zoom }) => {
      setMapCenter(center) 
      setMapZoom(zoom)
    }} */}
    <div className="asdf" >
      <ZoomControl/>
    </div>
      {gatas.map((gata, index)=>{
        return(
          <Marker key={index}
        width={50}
        anchor={gata.coord}
        color={gata?.color??color}
        onClick={()=>{setSidedata(gatas[index]);}}
      />
        )
      })}

  {/*<Marker 
        width={50}
        anchor={[3.45130509295518, -76.54298225026996]}
        color="#000"
        onClick={()=>{marketSelect(1);}}
      />

      <Overlay anchor={[50.879, 4.6997]} offset={[120, 79]}>
        <img src='/img/pigeon.jpg' width={240} height={158} alt='' />
  </Overlay>*/}

    </Map>
    </div>
    </div>
  );
}

export default App;
