import { useState } from "react";
import React from 'react';
import { Map, Marker, ZoomControl, /*Overlay*/ } from "pigeon-maps"
import { maptiler } from 'pigeon-maps/providers'

function App() {

  const maptilerProvider = maptiler('OeC7UjZWzAPnrhmLpPbX', 'basic-v2');
  const [mapCenter, setMapCenter] = useState([50.879, 4.6997])
  const [mapZoom, setMapZoom] = useState(11)
  const marketSelect = (type)=>{
      switch (type) {
        default:
        case 1:
          document.getElementById("demo").classList.add("show");
          break;
      }
  }

  return (
    <>
    <div className="hv-100">
    <Map
    provider={maptilerProvider}
    dprs={[1, 2]} // this provider supports HiDPI tiles
    height={"88vh"}
    defaultCenter={mapCenter}
    defaultZoom={mapZoom}
    onBoundsChanged={({ center, zoom }) => {
      setMapCenter(center) 
      setMapZoom(zoom)
    }}
    >
      <ZoomControl />
      <Marker 
        width={50}
        anchor={[50.879, 4.6997]} 
        onClick={()=>{marketSelect(1);}}
      />

      {/*<Overlay anchor={[50.879, 4.6997]} offset={[120, 79]}>
        <img src='/img/pigeon.jpg' width={240} height={158} alt='' />
  </Overlay>*/}

    </Map>
    </div>
    </>
  );
}

export default App;
