import React from "react";
import {useEffect} from 'react';

function Sidebar(props) {
  const slidedata = props.data;
  
  
  useEffect(() => {
    const keyDownHandler = event => {
      //console.log('User pressed: ', event.key);

      if (event.key === 'Escape') {
        event.preventDefault();

        hideitself();
      }
    };
    document.addEventListener('keydown', keyDownHandler);

    // 👇️ clean up event listener
    return () => {
      document.removeEventListener('keydown', keyDownHandler);
    };
  }, []);

  const [imageLoaded, setImageLoaded] = useState(true);
  const [retryCount, setRetryCount] = useState(0);
  useEffect(() => {
    const img = new Image();
    img.src = src;
    
    img.onload = () => {
      setImageLoaded(true);
    };

    img.onerror = () => {
      // Cuando hay un error, incrementa el contador y vuelve a intentarlo
      setRetryCount(retryCount + 1);
    };
  }, [slidedata.photoURL, retryCount]);

  const hideitself=()=>{
      document.getElementById("demo").classList.remove("show");
  }

  const sendform=()=>{
    alert("denuncias");
}


    return (
        <div className="offcanvas offcanvas-end" id="demo">
          <div className="offcanvas-header">
          <h1 className="offcanvas-title">{slidedata?.nombre}</h1>
          <button type="button" className="btn-close" onClick={hideitself}></button>
        </div>
        <div className="offcanvas-body">
        <div>
          {imageLoaded || retryCount >= 3 ? (
            <>
              <img className="w-100 h-50 img-thumbnail object-fit-cover" src={slidedata?.photoURL} alt={"fotografía de estatua de "+slidedata?.nombre}/>
            </>
          ) : (
            <p>Error al cargar la imagen. Reintentando...</p>
          )}
        </div>
          
          <p className="py-md mx-auto">
            {slidedata?.desc}
          </p>
          <div className="pt-3 d-flex">
          <video className="mx-auto w-auto h-50 object-fit-cover" controls>
            <source src={slidedata?.videoUrl} type="video/mp4"/>
            Your browser does not support HTML video.
          </video>
          </div>
        </div>
        <p onClick={sendform}>Informar</p>
      </div>
    );
}
export default Sidebar;
