import { useState } from "react";
import React from 'react';
import { Map, Marker } from "pigeon-maps"
import { maptiler } from 'pigeon-maps/providers'

function App() {

  const maptilerProvider = maptiler('OeC7UjZWzAPnrhmLpPbX', 'basic-v2');
  const [center, setCenter] = useState([50.879, 4.6997])
  const [CurrentCenter, setCCenter] = useState(center)
  const [zoom, setZoom] = useState(11)

  const marketSelect = (type)=>{
    alert("centro: "+center+" "+"CEntro incial: "+CurrentCenter);
    if(CurrentCenter==center){
      switch (type) {
        default:
        case 0:
          alert("sii");
          break;
      }
  }
  }

  return (
    <>

    <Map
    provider={maptilerProvider}
    dprs={[1, 2]} // this provider supports HiDPI tiles
    height={300}
    defaultCenter={center}
    defaultZoom={zoom}
    onBoundsChanged={({ center, zoom }) => { 
      setCenter(center) 
      setZoom(zoom) 
    }}
    >
      <Marker 
        width={50}
        anchor={[50.879, 4.6997]} 
        onClick={marketSelect} 
        onMouseOver={()=>{setCCenter(center);}}
      />
    </Map>    
    </>
  );
}

export default App;
