import React from "react";
import { useState } from "react";

import mouse from "../img/mouse.png"
function Mouse() {

    const imagen = document.getElementById('cursor');

    document.addEventListener('mousemove', (e) => {
    const x = e.clientX;
    const y = e.clientY;

    imagen.style.left = x + 'px';
    imagen.style.top = y + 'px';
    });

    imagen.addEventListener('click', () => {
    if (imagen.style.transform === 'scale(1.2)') {
        imagen.style.transform = 'scale(1)';
    } else {
        imagen.style.transform = 'scale(1.2)';
    }
    });

    return(
    <div class="container">
        <img src={mouse} id="cursor" class="mycursor" width={"auto"} height={"70px"}/>
    </div>
    );
}
export default Mouse;
