import React from "react";
import {useEffect, useState} from 'react';

function Sidebar(props) {
  const slidedata = props.data;
  
  
  useEffect(() => {
    const keyDownHandler = event => {
      if (event.key === 'Escape') {
        event.preventDefault();
        hideitself();
      }
    };
    document.addEventListener('keydown', keyDownHandler);
    return () => {
      document.removeEventListener('keydown', keyDownHandler);
    };


  }, []);

  const [imageURL, setImageSRC] = useState("");
  const [imageLoaded, setImageLoaded] = useState(false);
  useEffect(() => {
    const img = new Image();
    img.src = slidedata.photoURL;
    
    img.onload = () => {
      setImageLoaded(true);
      setImageSRC(slidedata.photoURL);
    };

    img.onerror = () => {

    };
  }, [slidedata]);

  const hideitself=()=>{
      document.getElementById("demo").classList.remove("show");
      setImageSRC("");
  }

  const sendform=()=>{
    alert("denuncias");
}


    return (
        <div className="offcanvas offcanvas-end" id="demo">
          <div className="offcanvas-header">
          <h1 className="offcanvas-title">{slidedata?.nombre}</h1>
          <button type="button" className="btn-close" onClick={hideitself}></button>
        </div>
        <div className="offcanvas-body element-con-scroll text-center">
        <div>
          {imageLoaded ? (
            <>
              <img className="img-thumbnail object-fit-cover my-5" src={imageURL} alt={"fotografía de estatua de "+slidedata?.nombre}/>
            </>
          ) : (
            <p>Cargando imagen...</p>
          )}
        </div>
          <p className="py-md mx-auto">
            {slidedata?.desc}
          </p>
          <div className="pt-3 d-flex">
          <video className="mx-auto w-auto h-50 object-fit-cover" controls>
            <source src={slidedata?.videoUrl} type="video/mp4"/>
            Tu navegador no soporta el elemento de video.
          </video>
          </div>
          <button className="my-3 btn btn-outline-info" onClick={sendform}>Informar Cambio de Ubicacion</button>
          
          <div class="row">
          <div class="input-group mb-3">
          <input type="text" class="form-control" placeholder="Coordenada X" />
          <input type="text" class="form-control" placeholder="Coordenada Y" />
          </div>
          <button class="btn bg-transparent disabled">
  <span class="spinner-border spinner-border-sm"></span>
</button>
          <input type="text" id="numeroInput" oninput="this.value = this.value.replace(/[^0-9]/g, '');"/>  
          
              <button class="btn btn-primary w-75 mx-auto" type="button">Enviar</button>
          </div>

        </div>
        
      </div>
    );
}
export default Sidebar;
