import React from "react";
import {useEffect} from 'react';

function Sidebar({props}) {

  useEffect(() => {
    const keyDownHandler = event => {
      //console.log('User pressed: ', event.key);

      if (event.key === 'Escape') {
        event.preventDefault();

        hideitself();
      }
    };
    document.addEventListener('keydown', keyDownHandler);

    // 👇️ clean up event listener
    return () => {
      document.removeEventListener('keydown', keyDownHandler);
    };
  }, []);

  const hideitself=()=>{
      document.getElementById("demo").classList.remove("show");
  }
  console.log("sidebar dice: "+JSON.stringify(props));


    return (
        <div className="offcanvas offcanvas-end" id="demo">
        <div className="offcanvas-header">
          <h1 className="offcanvas-title">{props.nombre}</h1>
          <button type="button" className="btn-close" onClick={hideitself}></button>
        </div>
        <div className="offcanvas-body">
          <p>{props.desc}</p>
          <video width="400" controls>
            <source src={props.videoUrl} type="video/mp4"/>
            Your browser does not support HTML video.
          </video>
        </div>
      </div>
    );
}
export default Sidebar;
