import { useState, useEffect } from "react";
import React from 'react';
import { Map, Marker, ZoomControl, /*Overlay*/ } from "pigeon-maps"
import { maptiler } from 'pigeon-maps/providers'

import Menu from './components/Menu';
import Sidebar from './components/Sidebar';
import Mouse from "./components/Mouse";

function App() {
  //son 34 gatas

  const maptilerProvider = maptiler('OeC7UjZWzAPnrhmLpPbX', 'basic-v2');
  const [mapCenter, setMapCenter] = useState([3.451245219418733, -76.54367205437333])
  const [mapZoom, setMapZoom] = useState(18)

  const color="#99F";
  const gatas = [
    {
      nombre:"Fogata",
      photoURL:"https://4.bp.blogspot.com/-FcB1YD9VFto/VKXZmJsMH-I/AAAAAAAAJVs/1j-U1JdVPbI/s1600/La%2Bgata%2BFogata%2B-%2BRoberto%2BMolano%2BGonz%C3%A1lez%2B-%2B3.JPG",
      coord: [3.451310447643775, -76.54317536931384],
      desc: "Esto es una descripción corta de la gata",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
    {
      nombre:"No hay gato",
      photoURL:"https://4.bp.blogspot.com/-A0qUYmdwCuo/VKXZxRFpyxI/AAAAAAAAJXM/5yBSZN7TluE/s1600/No%2Bhay%2BGato%2B-%2BWilson%2BD%C3%ADaz%2B-%2B2.JPG",
      coord: [3.4512182029859604, -76.54315780951954],
      desc: "Esto es una descripción corta de la gata",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
    {
      nombre:"Anabella",
      photoURL:"https://1.bp.blogspot.com/-aIO-jnlpwng/VKXZbtoiAQI/AAAAAAAAJUY/S4GV3U6jk2Y/s1600/Anabella%2Bla%2Bgata%2BSuperestrella%2B-%2BDiego%2BPombo%2B-%2B3.JPG",
      coord: [3.4512812413279104, -76.54341493194644],
      desc: "Esto es una descripción corta de la gata",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
    {
      nombre:"Vellocino de Oro",
      photoURL:"https://1.bp.blogspot.com/-uk1aPnEMw4o/VKXZtS2vdtI/AAAAAAAAJWs/1cRNxyZ6ov8/s1600/La%2Bgata%2BVellocino%2Bde%2Boro%2B-%2BJos%C3%A9%2BHoracio%2BMart%C3%ADnez%2B-%2B1.JPG",
      coord: [3.4511551646398115, -76.54351868310115],
      desc: "Esto es una descripción corta de la gata",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
    {
      nombre:"Gachuza",
      photoURL:"https://1.bp.blogspot.com/-x-9T_S8CiC8/VKXZoOO19HI/AAAAAAAAJV8/jnv45S8JvIo/s1600/La%2Bgata%2BGachuza%2B-%2BAngela%2BVillegas%2B-%2B1.JPG",
      coord: [3.4513127604973146, -76.54376227276875],
      desc: "Esto es una descripción corta de la gata",
      videoUrl: "https://www.w3schools.com/html/mov_bbb.mp4",
      postVideo: "",
    },
  ];
  const [sidedata, setSidedata] = useState({});
  useEffect(() => {
    if(Object.keys(sidedata).length !== 0){
      //console.log(sidedata.nombre)
      document.getElementById("demo").classList.add("show");
    }
  }, [sidedata]);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const handlemouse = (e)=>setPosition({x: e.clientX-35, y: e.clientY-35})
  document.body.onmousemove = handlemouse;
  return (
    <div className="bg-dark bg-gradient" style={{height: "100hv", width: "100wv"}}>
    <Mouse position={position}/>
    <Menu />
    <Sidebar data={sidedata}/>
    <div style={{height: "89.6vh"}}>
    <Map
    provider={maptilerProvider}
    dprs={[1, 2]}
    defaultCenter={mapCenter}
    defaultZoom={mapZoom}
    >
      {/*onBoundsChanged={({ center, zoom }) => {
      setMapCenter(center) 
      setMapZoom(zoom)
    }} */}
      <ZoomControl />
      {gatas.map((gata, index)=>{
        return(
          <Marker key={index}
        width={50}
        anchor={gata.coord}
        color={color}
        onClick={()=>{setSidedata(gatas[index]);}}
      />
        )
      })}
  {/*<Marker 
        width={50}
        anchor={[3.45130509295518, -76.54298225026996]}
        color="#000"
        onClick={()=>{marketSelect(1);}}
      />

      <Overlay anchor={[50.879, 4.6997]} offset={[120, 79]}>
        <img src='/img/pigeon.jpg' width={240} height={158} alt='' />
  </Overlay>*/}

    </Map>
    </div>
    </div>
  );
}

export default App;
